ANSI_RESET   = "\033[0m"
ANSI_BOLD    = "\033[1m"
ANSI_DIM     = "\033[2m"
ANSI_GREEN   = "\033[92m"
ANSI_YELLOW  = "\033[93m"
ANSI_CYAN    = "\033[96m"
